  props: ['subFieldIndex'],
  emits: ['setClass'],
  computed: {
    getRandomPathBySign() {
      const pathway = this[`${this.sign}Pathways`];
      const randomIndex = Math.floor(Math.random() * (pathway?.length ?? 1));
      return pathway ? pathway[randomIndex] : '';
    },
    getStyle() {
      return {fill: store?.getDefaultColorBySign(this.sign)}
    }
  },
  methods: {
    setSign() {
      if (this.isSetSign) return;

      this.sign = store.sign
      this.isSetSign = true
      this.$emit('setClass')
      store.swapSign()
      store.activeSubfieldIndex = this.subFieldIndex
    },
  },
}
